/**
 * n8n node script that generates a shell script to:
 * 1. Download videos and audio files
 * 2. Merge each video with its corresponding audio and add a text overlay
 * 3. Concatenate all merged videos into a final output optimized for YouTube
 */

// Extract input data from previous nodes
const titles = $('Split Out').all().map(item => item?.json?.title);
const videoUrls = $('Get Video').all().map(item => item?.json?.video?.url);
const audioUrls = $('Upload Mp3').all().map(item => item?.json?.webContentLink);

// Configuration options - optimized for YouTube
const config = {
  workDir: "/tmp/n8n",
  finalOutputName: "final_output.mp4",
  videoDuration: 5, // Each video is 5 seconds long
  video: {
    width: 720,
    height: 1280,
    frameRate: 30,
    pixelFormat: "yuv420p",
    bitrate: "4M" // 4 Mbps is good for 720p portrait video
  },
  ffmpeg: {
    videoCodec: "libx264",
    audioCodec: "aac",
    audioChannels: 2,
    audioBitrate: "192k", // YouTube recommends at least 128k, 192k is good
    preset: "medium" // Balance between encoding speed and quality
  },
  transitions: {
    fadeIn: {
      enabled: true,
      duration: 0.5 // Duration in seconds
    },
    fadeOut: {
      enabled: true,
      duration: 0.5 // Duration in seconds
    }
  },
  textOverlay: {
    fontColor: "white",
    fontSize: 48,
    boxColor: "black@0.5",
    boxBorderWidth: 10,
    topPosition: 20,
    lineSpacing: 10,
    font: "/usr/share/fonts/truetype/dejavu/DejaVuSans-Bold.ttf",
    maxTextLength: 25,
    textWidth: 80
  },
  metadata: {
    title: "Video Compilation",
    artist: "VASA Project",
    comment: "Created with n8n workflow",
    copyright: "© " + new Date().getFullYear()
  }
};

// Generate the FFmpeg text overlay filters for multiple lines
function generateTextFilters(lines, config) {
  if (!lines || lines.length === 0) return '';

  const baseY = config.textOverlay.topPosition / 100; // Base position from top as percentage
  const lineHeight = config.textOverlay.fontSize * 1.5 / config.video.height; // Line height as percentage of video height

  // Create individual drawtext filters for each line
  return lines.map((line, index) => {
    const yPosition = baseY + (index * lineHeight);

    // Escape single quotes by doubling them for FFmpeg
    const escapedText = line.replace(/'/g, "''");

    return `drawtext=text='${escapedText}':` +
           `x=(w-tw)/2:` +
           `y=h*${yPosition}:` +
           `fontcolor=${config.textOverlay.fontColor}:` +
           `fontsize=${config.textOverlay.fontSize}:` +
           `fontfile=${config.textOverlay.font}:` +
           `box=1:` +
           `boxcolor=${config.textOverlay.boxColor}:` +
           `boxborderw=${config.textOverlay.boxBorderWidth}:` +
           `fix_bounds=true`;
  }).join(',');
}

// Helper function to split title into lines
function splitTitleIntoLines(title, maxLength) {
  if (!title) return [''];

  // Normalize spaces and escape quotes for shell safety
  let escaped = title.replace(/"/g, '\\"').replace(/\s+/g, ' ').trim();

  // Split into words
  const words = escaped.split(' ').filter(word => word.length > 0);

  // Create lines respecting the provided maxLength parameter
  let lines = [];
  let currentLine = '';

  words.forEach(word => {
    if (currentLine.length + word.length + 1 > maxLength && currentLine.length > 0) {
      lines.push(currentLine);
      currentLine = word;
    } else {
      currentLine += (currentLine ? ' ' : '') + word;
    }
  });

  if (currentLine) {
    lines.push(currentLine);
  }

  // Only force splitting for very short text that's long enough to split
  if (lines.length === 1 && words.length >= 5 && lines[0].length > maxLength * 0.8) {
    const midPoint = Math.floor(words.length / 2);
    const firstLine = words.slice(0, midPoint).join(' ');
    const secondLine = words.slice(midPoint).join(' ');
    lines = [firstLine, secondLine];
  }

  return lines;
}

// Generate metadata arguments
function generateMetadataArgs(config) {
  const metadata = [];

  if (config.metadata.title) metadata.push(`-metadata title="${config.metadata.title}"`);
  if (config.metadata.artist) metadata.push(`-metadata artist="${config.metadata.artist}"`);
  if (config.metadata.comment) metadata.push(`-metadata comment="${config.metadata.comment}"`);
  if (config.metadata.copyright) metadata.push(`-metadata copyright="${config.metadata.copyright}"`);

  return metadata.join(' ');
}

// Generate the shell script header
let script = `#!/bin/sh
set -e

# Create working directory
WORK_DIR="${config.workDir}"
mkdir -p $WORK_DIR
cd $WORK_DIR

# Clean previous files if they exist
rm -rf $WORK_DIR/video_* $WORK_DIR/audio_* $WORK_DIR/merged_* $WORK_DIR/file_list.txt $WORK_DIR/${config.finalOutputName}

# Download videos and audio files
echo "Downloading videos and audio files..."
`;

// Generate processing steps for each video/audio pair
for (let i = 0; i < titles.length; i++) {
  // Split the title into multiple lines
  const titleLines = splitTitleIntoLines(titles[i] || `Video ${i}`, config.textOverlay.maxTextLength);

  // Generate FFmpeg text filters for all lines
  const textFilters = generateTextFilters(titleLines, config);

  script += `
# Processing item ${i}
VIDEO_URL="${videoUrls[i]}"
AUDIO_URL="${audioUrls[i]}"

echo "Downloading video ${i}: $VIDEO_URL"
curl -L "$VIDEO_URL" -o "$WORK_DIR/video_${i}.mp4"

echo "Downloading audio ${i}: $AUDIO_URL"
curl -L "$AUDIO_URL" -o "$WORK_DIR/audio_${i}.mp3"

# Merge video with audio and add text overlay, ensuring YouTube-compatible format
echo "Processing video ${i} with title: ${titleLines.join(' ')}"
ffmpeg -y -i "$WORK_DIR/video_${i}.mp4" -i "$WORK_DIR/audio_${i}.mp3" \\
  -map 0:v -map 1:a \\
  -c:v ${config.ffmpeg.videoCodec} -c:a ${config.ffmpeg.audioCodec} \\
  -r ${config.video.frameRate} -b:v ${config.video.bitrate} \\
  -pix_fmt ${config.video.pixelFormat} \\
  -ac ${config.ffmpeg.audioChannels} -b:a ${config.ffmpeg.audioBitrate} \\
  -s ${config.video.width}x${config.video.height} \\
  -preset ${config.ffmpeg.preset} \\
  -vf "scale=${config.video.width}:${config.video.height},setsar=1:1,fade=t=in:st=0:d=${config.transitions.fadeIn.duration},fade=t=out:st=${config.videoDuration - config.transitions.fadeOut.duration}:d=${config.transitions.fadeOut.duration},${textFilters}" \\
  ${generateMetadataArgs(config)} \\
  "$WORK_DIR/merged_${i}.mp4"

# Add to file list
echo "file '$WORK_DIR/merged_${i}.mp4'" >> "$WORK_DIR/file_list.txt"
`;
}

// Generate final concatenation and output info
script += `
# Concatenation with transition handling
echo "Creating final concatenated video optimized for YouTube..."

# Two-pass encoding for better quality at the same bitrate
echo "Performing two-pass encoding for best YouTube quality..."
ffmpeg -y -f concat -safe 0 -i "$WORK_DIR/file_list.txt" \\
  -c:v ${config.ffmpeg.videoCodec} -b:v ${config.video.bitrate} \\
  -pix_fmt ${config.video.pixelFormat} -r ${config.video.frameRate} \\
  -c:a ${config.ffmpeg.audioCodec} -b:a ${config.ffmpeg.audioBitrate} -ac ${config.ffmpeg.audioChannels} \\
  -s ${config.video.width}x${config.video.height} \\
  -preset ${config.ffmpeg.preset} -movflags +faststart \\
  ${generateMetadataArgs(config)} \\
  -pass 1 -f mp4 /dev/null

ffmpeg -y -f concat -safe 0 -i "$WORK_DIR/file_list.txt" \\
  -c:v ${config.ffmpeg.videoCodec} -b:v ${config.video.bitrate} \\
  -pix_fmt ${config.video.pixelFormat} -r ${config.video.frameRate} \\
  -c:a ${config.ffmpeg.audioCodec} -b:a ${config.ffmpeg.audioBitrate} -ac ${config.ffmpeg.audioChannels} \\
  -s ${config.video.width}x${config.video.height} \\
  -preset ${config.ffmpeg.preset} -movflags +faststart \\
  ${generateMetadataArgs(config)} \\
  -pass 2 "$WORK_DIR/${config.finalOutputName}"

echo "Final video created at: $WORK_DIR/${config.finalOutputName}"
ls -la "$WORK_DIR/${config.finalOutputName}"

# Get file size
echo "File size:"
du -h "$WORK_DIR/${config.finalOutputName}"

# Get video information
echo "Video information:"
ffprobe -v quiet -print_format json -show_format -show_streams "$WORK_DIR/${config.finalOutputName}"

# Print the location for further use
echo "FINAL_VIDEO_PATH=$WORK_DIR/${config.finalOutputName}"
`;

// Return the generated shell script
return { command: script };
