// входить {message} з Telegram
const raw = $json?.message?.text || '';
const text = raw.trim();
return [{ json: {
  brand: 'Demo Site',
  lang: 'uk-UA',
  tz: 'Europe/Kyiv',
  brief: text.slice(0, 8000) // обмежимо, щоб не рвало
}}];
