parameters.jsonBody:
={
  "post": {
    "target": {
      "targetType": "tiktok",
      "isYourBrand": false,
      "disabledDuet": false,
      "privacyLevel": "PUBLIC_TO_EVERYONE",
      "isAiGenerated": false,
      "disabledStitch": false,
      "disabledComments": false,
      "isBrandedContent": false
    },
    "content": {
      "text": {{ $('Prepare for Publish').item.json.final_text_long.toJsonString() }},
      "platform": "tiktok",
      "mediaUrls": ["{{ $json.url }}"]
    },
    "accountId": "{{ $('Prepare for Publish').item.json.tiktok_id }}"
  }
}

={{ JSON.stringify({
  video_inputs: [
    {
      character: {
        type: "avatar",
        avatar_id: $json.avatar_id,
        avatar_style: "normal",
        scale: 1.0,
        offset: {
          x: 0.0,
          y: 0.0
        },
        matting: false
      },
      voice: {
        type: "text",
        input_text: $('AI Agent').item.json.output,
        voice_id: $json.voice_id,
        speed: 1.1,
        pitch: 50,
        emotion: "Excited"
      },
      background: {
        type: "video",
        url: $json.background_video_url,
        play_style: "loop",
        fit: "cover"
      }
    }
  ],
  dimension: {
    width: 720,
    height: 1280
  },
  aspect_ratio: "9:16",
  caption: false,
  title: $('Write Title').item.json.message.content
}) }}
