const update = $input.first().json;
let result = [];

if (update.message) {
  const message = update.message.text;
  const chatId = update.message.chat.id;

  if (message.startsWith('/react ')) {
    const prompt = message.replace('/react ', '');
    result.push({
      technology: 'react',
      prompt: prompt,
      chatId: chatId
    });
  } else if (message.startsWith('/html ')) {
    const prompt = message.replace('/html ', '');
    result.push({
      technology: 'html', 
      prompt: prompt,
      chatId: chatId
    });
  } else if (message === '/start') {
    result.push({
      technology: 'help',
      prompt: '–ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ:\n/react [–≤–∞—à –ø—Ä–æ–º–ø—Ç] - –¥–ª—è React\n/html [–≤–∞—à –ø—Ä–æ–º–ø—Ç] - –¥–ª—è HTML',
      chatId: chatId
    });
  }
}

return result;

